import type { FC, createContext } from "react";

import styles from "./TodoData.module.scss";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useContext, useState } from "react";
import { MyContextData } from "../index";

const TodoData: FC = () => {
  const { data, setIsDeleted, isDeleted } = useContext<any>(MyContextData);
  const [isSelected, setIsSelected] = useState<string>("#847f7f");

  const handelSelected = (value: number) => setIsDeleted(value);

  return (
    <div className={styles.wrapper}>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} size="small" aria-label="a dense table">
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell align="right">Age</TableCell>
              <TableCell align="right">Subscription</TableCell>
              <TableCell align="right">Employment</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data &&
              data.map((element: any) => (
                <TableRow
                  key={element.id}
                  sx={{
                    "&": {
                      border: 0,
                      cursor: "pointer",
                      background:
                        element.id === isDeleted ? "#847f7f" : "transparent",
                    },
                  }}
                  onClick={() => {
                    handelSelected(element.id);
                    setIsSelected("red");
                  }}
                >
                  <TableCell component="th" scope="row">
                    {element.name}
                  </TableCell>
                  <TableCell align="right">{element.age}</TableCell>
                  <TableCell align="right">{element.subscription}</TableCell>
                  <TableCell align="right">{element.employment}</TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default TodoData;
