import { type FC, type ChangeEvent, useContext, useState } from "react";

import {
  CheckboxComponent,
  Input,
  NumberInputComponent,
  SelectComponent,
  SwitchComponent,
} from "components";
import styles from "./TodoInsert.module.scss";
import { EducationalData } from "../../components/SelectComponent/utils";

import { MyContextData } from "../index";
import classNames from "classnames";

const TodoInsert: FC = () => {
  const { setData, data, isDeleted, setIsDeleted } =
    useContext<any>(MyContextData);
  const [name, setName] = useState<string>("");
  const [age, setAge] = useState<number | string>("");
  const [isSubscription, setIsSubscription] = useState<string>("");
  const [isEmployed, setIsEmployed] = useState<boolean>(false);

  const isInsert = name !== "" && age !== "" && isSubscription !== "";

  console.log(isInsert);

  const item = {
    id: Math.round(new Date().getTime()),
    name: name,
    age: age,
    subscription: isSubscription,
    employment: isEmployed ? "Employed" : "Unemployed",
  };

  console.log(isDeleted);

  const handelName = (event: ChangeEvent<HTMLInputElement>) =>
    setName(event.target.value);

  const handelAge = (event: any) => setAge(event.target.value);

  const handelSubscription = (value: string) => setIsSubscription(value);

  const handelEmployed = (event: any) => setIsEmployed(event.target.checked);

  const handelIDataInsert = () => {
    setData([...data, item]);
    setName("");
    setAge("");
    setIsSubscription("");
    setIsSubscription("");
  };

  const handelDeleted = (value: number) =>
    data.filter((element: any) => element.id !== value);

  return (
    <div className={styles.wrapper}>
      <p className={styles.wrapper__title}>Insert Row</p>
      <Input placeholder="Name" handelChange={handelName} value={name} />
      <NumberInputComponent handelChange={handelAge} value={age} />
      <SelectComponent
        items={EducationalData}
        placeholder="select place"
        handelChange={handelSubscription}
      />
      <CheckboxComponent label="Employed" handelChange={handelEmployed} />
      <button
        className={classNames(styles.wrapper__button, {
          [styles.disabled]: !isInsert,
        })}
        onClick={() => {
          if (isInsert) handelIDataInsert();
        }}
      >
        Insert
      </button>
      <hr />
      <SwitchComponent text="Mode" />
      <button
        className={classNames(styles.wrapper__button, {
          [styles.disabled]: isDeleted === 0,
        })}
        onClick={() => {
          setData(handelDeleted(isDeleted));
          setIsDeleted(0);
        }}
      >
        Delete
      </button>
    </div>
  );
};
export default TodoInsert;
