import {
  type FC,
  type ChangeEvent,
  useContext,
  useState,
  useCallback,
  useMemo,
  FormEvent,
} from "react";

import {
  CheckboxComponent,
  Input,
  NumberInputComponent,
  SelectComponent,
  SwitchComponent,
} from "components";
import classNames from "classnames";

import styles from "./TodoInsert.module.scss";
import { EducationalData } from "../../components/SelectComponent/utils";

import { MyContextData } from "../index";

const TodoInsert: FC = () => {
  const { setData, data, selectedRow, setSelectedRow } =
    useContext(MyContextData);
  const [name, setName] = useState<string>("");
  const [age, setAge] = useState<number>(0);
  const [isSubscription, setIsSubscription] = useState<string>("");
  const [isEmployed, setIsEmployed] = useState<boolean>(false);

  const isInsert = useMemo(
    () => name !== "" && age !== 0 && isSubscription !== "",
    [isSubscription, age, name]
  );

  const onSubmitHandler = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!isInsert) return;

    const newTodo = {
      id: Math.round(new Date().getTime()),
      name: name,
      age: age,
      subscription: isSubscription,
      employment: isEmployed ? "Employed" : "Unemployed",
    };

    if (setData) setData((prev) => [...prev, newTodo]);
    setName("");
    setAge(0);
    setIsSubscription("");
  };

  const handleEmployed = useCallback(
    (event: ChangeEvent<HTMLInputElement>) =>
      setIsEmployed(event.target.checked),
    []
  );

  const handelDeleted = (value: number) => {
    if (data && setData && setSelectedRow) {
      const newData = data.filter((element) => element.id !== value);
      setData(newData);
      setSelectedRow(0);
    }
  };
  return (
    <div className={styles.wrapper}>
      <form className={styles.wrapper} onSubmit={onSubmitHandler}>
        <p className={styles.wrapper__title}>Insert Row</p>
        <Input
          placeholder="Name"
          handelChange={(event: ChangeEvent<HTMLInputElement>) =>
            setName(event.target.value)
          }
          value={name}
        />
        <NumberInputComponent
          value={age}
          handelChange={(event: ChangeEvent<HTMLInputElement>) =>
            setAge(Number(event.target.value))
          }
          handleIncrement={() => setAge((prev) => prev + 1)}
          handleDecrement={() => setAge((prev) => prev - 1)}
        />
        <SelectComponent
          items={EducationalData}
          placeholder="select place"
          handelChange={(value: string) => setIsSubscription(value)}
        />
        <CheckboxComponent label="Employed" handelChange={handleEmployed} />
        <button
          type="submit"
          className={classNames(styles.wrapper__button, {
            [styles.disabled]: !isInsert,
          })}
        >
          Insert
        </button>
      </form>
      <hr />
      <SwitchComponent text="Mode" />
      <button
        className={classNames(styles.wrapper__button, {
          [styles.disabled]: selectedRow === 0,
        })}
        onClick={() => {
          if (selectedRow) handelDeleted(selectedRow);
        }}
      >
        Delete
      </button>
    </div>
  );
};
export default TodoInsert;
