import { FC } from "react";

import styles from "./switchComponent.module.scss";
import Switch from "@mui/material/Switch";
import { styled } from "@mui/material";

const PinkSwitch = styled(Switch)(({ theme }) => ({
  "& .MuiSwitch-switchBase.Mui-checked": {
    color: "black",
    width: "70px", // Add custom width for the switch base when checked
  },
  "& .MuiSwitch-switchBase.Mui-checked + .MuiSwitch-track": {
    backgroundColor: "green",
    width: "70px", // Add custom width for the track when checked
  },
}));

const SwitchComponent: FC = () => {
  return (
    <div>
      <PinkSwitch defaultChecked color="primary" />
    </div>
  );
};
export default SwitchComponent;
