import { FC } from "react";

import styles from "./switchComponent.module.scss";
import Switch from "@mui/material/Switch";
import { styled } from "@mui/material";

const PinkSwitch = styled(Switch)(({ theme }) => ({
  "& .MuiSwitch-switchBase.Mui-checked": {
    color: "black",
    "&.Mui-focusVisible": {
      backgroundColor: "green", // Change the background color when checked
    },
  },
  "& .MuiSwitch-switchBase.Mui-checked + .MuiSwitch-track": {
    backgroundColor: "green",
  },
  "& .css-1yjjitx-MuiSwitch-track": {
    backgroundColor: "green", // Change the background color of the track
    width: "70px",
  },
}));

const label = { inputProps: { "aria-label": "Color switch demo" } };

const SwitchComponent: FC = () => {
  return (
    <div>
      <PinkSwitch defaultChecked color="primary" {...label} />
    </div>
  );
};
export default SwitchComponent;
