import type { FC } from "react";

import Container from "./container";
import { ThemeProvider, useTheme } from "./mode/themContext";

const App: FC = () => {
  const { theme, toggleTheme } = useTheme();
  return (
    <>
      <ThemeProvider>
        <Container />
      </ThemeProvider>
    </>
  );
};
export default App;
